package Loop;

public class Evenodd {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		 for (int i=1;i<=10;i++)
	        {
	            if(i % 2 ==0)
	             System.out.println(i +" even");
	            else
	                System.out.println(i +" odd");
	        }

	        int b = 10;
	        int a = Integer.valueOf(b);
	        System.out.println("a="+a);
	    }
	}

	
